struct AudioSource {
    player: Arc<Mutex<Player>>
}

impl Iterator for AudioSource {
    type Item = f32;

    fn next(&mut self) -> Option<f32> {
        let mut player = self.player.lock().unwrap();
        let mut output = 0.0;
        if player.note_playing {
            let step = player.step;
            for voice in &mut player.voices {
                output += voice.generate(step, 0.0, 1);
            }
            player.step += 1;
        }
        Some(0.08*output)
    }
}

impl Source for AudioSource {
    fn channels(&self) -> u16 {
        return 1;
    }

    fn sample_rate(&self) -> u32 {
        return SAMPLE_RATE as u32;
    }

    fn total_duration(&self) -> Option<Duration> {
        return None;
    }

    fn current_frame_len(&self) -> Option<usize> {
        return None;
    }
}

fn process_midi_message(_timestamp: u64, message: &[u8], data: &mut Arc<Mutex<Player>>) {
    let mut player = data.lock().unwrap();
    if message[0] == 144 {
        player.current_note = message[1];
        player.note_playing = true;
        let frequency = 440.0 * f32::powf(2.0, (player.current_note as i32-69) as f32/12.0);
        for voice in &mut player.voices {
            voice.set_frequency(frequency);
        }
    }
    else if message[0] == 128 && player.current_note == message[1] {
        player.note_playing = false;
    }
}

struct MainGui {
    player_ref: Arc<Mutex<Player>>
}

impl App for MainGui {
    fn update(&mut self, ctx: &egui::Context, _frame: &mut eframe::Frame) {
        let mut player = self.player_ref.lock().unwrap();
        CentralPanel::default().show(ctx, |ui| {
            let mut shape_changed = false;
            let old_part = player.voice_part;
            let old_voices = player.voice_count;
            ui.horizontal(|ui| {
                egui::ComboBox::from_label("Voice Part")
                    .selected_text(format!("{:?}", player.voice_part))
                    .show_ui(ui, |ui| {
                        ui.selectable_value(&mut player.voice_part, VoicePart::Soprano, "Soprano");
                        ui.selectable_value(&mut player.voice_part, VoicePart::Alto, "Alto");
                        ui.selectable_value(&mut player.voice_part, VoicePart::Tenor, "Tenor");
                        ui.selectable_value(&mut player.voice_part, VoicePart::Bass, "Bass");
                    });
                ui.add_space(10.0);
                ui.add(egui::Slider::new(&mut player.voice_count, 1..=8).text("Voices"));
                ui.add_space(10.0);
                if ui.checkbox(&mut player.nasal, "Nasal").changed() {
                    shape_changed = true;
                }
            });
            egui::Grid::new("weights").show(ui, |ui| {
                let mut index = 0;
                let mut last_prefix = "set1";
                for (key, value) in player.weights.iter_mut() {
                    if !key.starts_with(last_prefix) {
                        if index > 0 {
                            ui.end_row();
                        }
                        index = 0;
                        last_prefix = &key[..4];
                    }
                    else {
                        index += 1;
                        if index%3 == 0 {
                            ui.end_row();
                        }
                    }
                    if ui.add(egui::Slider::new(value, 0.0..=1.0).text(*key)).dragged() {
                        shape_changed = true;
                    }
                    ui.add_space(10.0);
                }
                ui.end_row();
                for handle in player.handles {
                    ui.label(format!("{:.3}, {:.3}", handle.x, handle.y));
                }
            });
            let painter = ui.painter();
            let bounds = ui.available_rect_before_wrap();
            let black_stroke = epaint::Stroke::new(1.0, egui::Color32::BLACK);
            let gray_stroke = epaint::Stroke::new(1.0, egui::Color32::GRAY);
            painter.hline(egui::Rangef::new(bounds.min.x, bounds.max.x), bounds.max.y, black_stroke);
            painter.hline(egui::Rangef::new(bounds.min.x, bounds.max.x), (bounds.min.y+bounds.max.y)/2.0, gray_stroke);

            // Draw the handles.

            let response = ui.response();
            for (i, handle) in player.handles.iter_mut().enumerate() {
                let mut pos = pos2(bounds.min.x + handle.x*(bounds.max.x-bounds.min.x), (bounds.min.y+bounds.max.y)/2.0 - 0.2*handle.y*(bounds.max.y-bounds.min.y));
                let point_rect = Rect::from_center_size(pos, Vec2::new(5.0, 5.0));
                let point_id = response.id.with(i);
                let point_response = ui.interact(point_rect, point_id, Sense::drag());
                painter.add(Shape::circle_filled(pos, 5.0, egui::Color32::GRAY));
                pos += point_response.drag_delta();
                handle.x = (pos.x-bounds.min.x)/(bounds.max.x-bounds.min.x);
                handle.y = -(pos.y-(bounds.min.y+bounds.max.y)/2.0)/(0.2*(bounds.max.y-bounds.min.y));
                handle.x = f32::max(0.0, f32::min(1.0, handle.x));
                if point_response.dragged() {
                    shape_changed = true;
                }
            }

            // Draw the curves.

            let mut points1 = Vec::new();
            let mut points2 = Vec::new();
            let xscale = (bounds.max.x-bounds.min.x) / player.shape.len() as f32;
            let yscale = (bounds.max.y-bounds.min.y) / 10.0;
            for i in 0..player.shape.len() {
                let x = bounds.min.x + i as f32 * xscale;
                let y1 = bounds.max.y - player.base_shape[i]*yscale;
                let y2 = bounds.max.y - player.shape[i]*yscale;
                points1.push(egui::Pos2::new(x, y1));
                points2.push(egui::Pos2::new(x, y2));
            }
            painter.line(points1, gray_stroke);
            painter.line(points2, black_stroke);

            if shape_changed {
                player.update_shape();
            }
            if old_part != player.voice_part || old_voices != player.voice_count {
                player.rebuild();
                player.update_shape();
            }
        });
    }
}

fn main() -> Result<(), eframe::Error> {
    let player = Arc::new(Mutex::new(Player::new()));
    let source = AudioSource{ player: Arc::clone(&player) };
    let (_stream, handle) = OutputStream::try_default().unwrap();
    let _result = handle.play_raw(source.convert_samples());

    let midi_in = MidiInput::new("Chorus").unwrap();
    let in_ports = midi_in.ports();
    let in_port = &in_ports[1];
    let _conn_in = midi_in.connect(
        in_port,
        "midir-read-input",
        process_midi_message,
        Arc::clone(&player),
    ).unwrap();
    let options = NativeOptions::default();
    let gui = MainGui {
        player_ref: Arc::clone(&player)
    };
    eframe::run_native(
        "Chorus",
        options,
        Box::new(|_cc| Ok(Box::new(gui))),
    )
}
